# Check if running on macOS
if [[ "$OSTYPE" == "darwin"* ]]; then
    HOSTS_FILE="/etc/hosts"
    echo "âœ… Detected macOS"
else
    echo "âŒ This script is designed for macOS. Please add hosts entries manually."
    exit 1
fi

# Check if running as root (needed for hosts file)
if [ "$EUID" -ne 0 ]; then 
    echo "âš ï¸  This script needs sudo to modify /etc/hosts"
    echo "   You can also manually add these entries:"
    echo ""
    echo "   127.0.0.1  site1.localwp"
    echo "   127.0.0.1  site2.localwp"
    echo ""
    read -p "Continue with sudo? (y/n) " -n 1 -r
    echo
    if [[ ! $REPLY =~ ^[Yy]$ ]]; then
        exit 1
    fi
fi

# Add hosts entries
echo "ðŸ“ Adding hosts entries..."

if grep -q "site1.localwp" "$HOSTS_FILE"; then
    echo "   âš ï¸  site1.localwp already exists in hosts file"
else
    if [ "$EUID" -eq 0 ]; then
        echo "127.0.0.1  site1.localwp" >> "$HOSTS_FILE"
        echo "   âœ… Added site1.localwp"
    else
        echo "127.0.0.1  site1.localwp" | sudo tee -a "$HOSTS_FILE" > /dev/null
        echo "   âœ… Added site1.localwp"
    fi
fi

if grep -q "site2.localwp" "$HOSTS_FILE"; then
    echo "   âš ï¸  site2.localwp already exists in hosts file"
else
    if [ "$EUID" -eq 0 ]; then
        echo "127.0.0.1  site2.localwp" >> "$HOSTS_FILE"
        echo "   âœ… Added site2.localwp"
    else
        echo "127.0.0.1  site2.localwp" | sudo tee -a "$HOSTS_FILE" > /dev/null
        echo "   âœ… Added site2.localwp"
    fi
fi

